INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (13, 1, E'0', E'0', E'Damaged', E'0', E'13.1 > On top, block of white marble. 30,5 x 143,5 x 66 cm. Corners of north-west and south-east sides missing. Chipped and injured. On front and left sides moulding at square angles, forming corner of an entablature. The measurements of the plain lower side are 113 x 31 cm, indicating that the piece grew larger and longer going upwards. Bottom plain except for dowel hole in back (8 x 3 cm). Right side also plain, as is top apart from three shallow dowel holes of 2 ½, 4, and 1 ½ cm deep and maybe a fourth one at the proper right side (3 cm deep). The left and front sides show the same moulding becoming wider going upwards and beveled at the top. Moulding from bottom to top: cyma reversa, dentil, cyma recta almost twice the height of the cyma below, half hollow with drooping lower rim, abacus with egg and dart, and finally plain cornice.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 04:43:31', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (13, 2, E'0', E'0', NULL, E'0', E'Tufa block underneath is part of an architectonic structure. 30 x 170 x 48 cm. All surfaces in sight are plain except for west side, which is roughly worked with an axe. Bottom invisible. It looks similar to the foundation blocks in Site 25.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 04:46:59', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (14, 1, E'0', E'0', NULL, E'0', E'Tufa block. Architectural element with three plain sides. Bottom invisible, but seems plain as well as the side now facing east. Top worn, other three sides broken off. 45 x 122 x 45 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 04:52:13', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (15, 1, E'0', E'0', E'Damaged', E'0', E'Marble architectural element showing a carved decoration with scrolls facing the Via Appia. Surface very worn. The side facing up had a rectangular indent along the back. West corner of back broken off. 28 x 115 x 34 cm. The scroll decoration looks like an alternating pattern of flower buds and rosettes. It has for windings, starting on the left hand top side, running downwards from the crown of two long and thin acanthus leaves. The stem continues after a ring and finishes into an acanthus leaf at the bottom, where it continues with a volte ending in a lotus flower. The second winding ends in a crown of leaves turning from the bottom to the left. The third winding turns from the top to the left in a bud. The fourth winding comes down from the top and looks like the second, though turning to the right.', NULL, NULL, NULL, NULL, NULL, NULL, '2014-08-13 04:53:07', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (16, 1, E'0', E'0', E'Damaged', E'0', E'Site 16: Rectangular tufa block. Worn, originally having at least four plain sides, now damaged (actual top, south, east and west sides). North west corner broken off. Bottom not visible. 37 x 58 x 113.5 cm (as visible).', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 04:55:25', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (17, 1, E'0', E'0', NULL, E'0', E'Rectangular tufa block. Architectural element. 23 (as visible) x 63 x 83,5 cm. Lower side invisible.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 04:57:39', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (18, 1, E'0', E'0', E'Damaged', E'0', E'Eight blocks\r\n18.1 > Corner of tufa block. Broken off at east side. North, west sides and bottom worked. 27 (as visible) x 63 x 38 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:00:44', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (18, 2, E'0', E'0', E'Damaged', E'0', E'18.2 > Rectangular travertine block. Broken off at sides. Covered with grey crust. Surface is roundish. Shape of lunula in slight relief visible at one side. Rough chisel hole. Probably not entirely worked. The piece might belong to a statue and been broken off.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:01:57', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (18, 3, E'0', E'0', NULL, E'0', E'18.3 > rectangular tufa bock. 49 x 59 x 74 cm. Top and east side plain. North and west sides roughly worked. Lower side invisible. Depression in south side, indicating that the block could have functioned as an altar.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:05:43', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (18, 4, E'0', E'0', E'Damaged', E'0', E'18.4 > Rectangular tufa block. 44 x 144 x 57 cm. South side damaged or roughly worked. Bottom and west, north and east sides plain. Top roughly worked. Hole of 3 cm deep and 6 cm diameter in east side.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:07:21', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (18, 5, E'0', E'0', NULL, E'0', E'18.5 > Rectangular travertine block. One side visible. 56 x 90 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:08:56', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (18, 6, E'0', E'0', E'Damaged', E'0', E'18.6 > Marble slab lying topside down on top of 18.3 and 18.4. 36 x 95 x 82 cm. Top, bottom and north side plain. Square hole (5 x 2.5 x 2.5 cm) in bottom. On west side, the plain surface stops at 5 cm from the edge. West side roughly worked. South side broken off. Moulding on east side, from proper top to bottom: cornice with stylized acanthus leaves (.5 x 4 x 3 cm), dentils (2.5 x 3.5 cm; dentils 6 cm, interspace 3 cm); scotia filled with a frieze of alternating acanthus leaves and rosettes (12 cm wide) with many small drill holes; plain vertical band of 40 cm at north side, partly alternating with floral motif of, alternating, rosettes composed of four petals and airs of leaves hold by a ring (5 cm); cornice with stylized lotuses and palmettes (14 cm); 5 cm deep indent (3 cm high).', E'Slab', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:11:19', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (18, 7, E'0', E'0', NULL, E'0', E'18.7 > Marble slab like 18.6. Together they belong to an entablature of a funerary monument, with 18.6 in the centre and 18.7 at a corner. 18.7 is almost square with an extension on one side. Some small differences in respect to 18.6 are noticeable, namely that the dentils are wider (7-8 cm). This implies that more workmen were active in making the monument. The drilling technique seems to be an indication for the middle of the first century AD.', E'Slab', NULL, NULL, NULL, E'Mid 1st c. A.D.', NULL, '2014-08-13 05:16:53', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (19, 1, E'0', E'0', E'Damaged', E'0', E'Two blocks.\r\n19.1 > Rectangular marble block on top of 19.2. Surface injured and corners rounded. Irregular shape. 57 x 51 x 79 cm. Top in almost horizontal position, roughly worked and less wide (31 cm instead of 51 cm). All sides worked with shallow relief indicating drapery. This means that the fragment formed part of a funerary statue.', E'Block', E'Statue', NULL, NULL, NULL, NULL, '2014-08-13 05:20:07', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (19, 2, E'0', E'0', NULL, E'0', E'19.2 > Rectangular tufa block. 29 x 90 x 95 cm. Protrusions visible on east side, 28 cm on south side and 17 cm on north side. Top and east side roughly cut. Two holes in east side and one hole in west side.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:30:28', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (20, 1, E'0', E'0', E'Damaged', E'0', E'Fragment of marble sculpture. Broken off at top and bottom and left side. Back missing. Covered with crust of dark dirt. 140 à 150 x 26 à 33 x 56 cm. Draped female figure standing next to square pillar, of which the base with three protrusions of 28 cm is visible and the top lacks. The horizontal folds of the upper part of the stola cover the arm bent in front of the chest. The lower part of the drapery has slanting folds covering hips and legs.', E'Sculpture', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:31:58', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (21, 1, E'0', E'0', E'Damaged', E'0', E'Fragment of marble sculpture. Flat south side an bottom. Concave side from west to east with shallow remains of drapery. 14 à 29 x 33 x 52 cm. The actual north side seems to be the original bottom and the block may represent the right knee of a seated person in full dress. Because of the smooth side, the figure was either composed of various fragments or the piece was hewn off and reused as a building block.\r\nWe should figure out whether it belongs to the statue fragment of site 161.', E'Sculpture', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:33:37', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (22, 1, E'0', E'0', NULL, E'0', E'Fragment of marble sculpture. Surface worn; shape hardly recognisable. 24 x 100 x 61 cm.', E'Sculpture', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:35:09', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (23, 1, E'0', E'0', E'Damaged', E'0', E'Square block of tufa with rounded angle. East side broken off. Worn off. 41 x 57 x 47 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:54:00', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (24, 1, E'0', E'0', E'Damaged', E'0', E'Rectangular travertine block. Top smooth. East and west sides roughly worked. Boss protruding on west side. Other sides broken off. Hole carved in east side. Partly interred. 31 x 108 x 28 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:55:23', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (25, 1, E'0', E'0', E'Damaged', E'0', E'Wall of six tufa blocks belonging to an extended monument (see site 32). Surface worn and chipped. Upper corner of southern side broken off. Two layers of three blocks each visible, running north-south. Lower partly interred tier has roughly worked west and east faces and a flat top. Maximally visible height is 58 cm, with 430 x 60 x 62 cm in total. Two blocks of the upper layer are broken. The northern block is bossed and has its topside beveled. Dove-tail holes on top. The east side has a rough central surface surrounded by a smooth rim (rustica). Oval cavity of 8 x 7 cm visible in east side (man-made or natural?). 54 (as visible) x 60.5 x 64 cm.\r\nIt is in line with site 32.\r\nExcavations have been carried out from 2011.', E'Wall structure', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:56:39', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (26, 1, E'0', E'0', NULL, E'0', E'Two blocks of tufa, mostly interred\r\n26.1 > Left block partly smooth, partly roughly worked. 13 (as visible) x 47 x 37 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 05:59:10', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (26, 2, E'0', E'0', E'Damaged', E'0', E'26.2 > Right block broken into two pieces in the length. 20 (as visible) x 39 (as visible) x 90 (as visible) cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 06:01:47', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (27, 1, E'0', E'0', E'Complete', E'0', E'Tufa threshold. Complete. Broken into two parts. Back roughly worked. Sides and front rather roughly worked, but with a finer chisel. Bottom invisible. Top flattened. Tufa. 35 x 216 x 66 cm. Rectangular block with rectangular cavity in top (19 x 81 x 43 cm). At front side (west) entrance (11 x 62 x 17) cm with hole for cardo at south side. Dovetail holes in top at south and north for insertion of lead clamps connecting the block with other blocks. Other holes in northern section. Two bosses with tiny holes visible on left and right of door entrance, which might be rustica decorations. In the southern part of the cavity a later channel is visible with a round form at the east side, clearly the attachment with a water conduct. The channel might serve to abduct water.', E'Edifice', NULL, NULL, NULL, NULL, NULL, '2014-08-13 06:04:19', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (28, 1, E'0', E'0', NULL, E'0', E'Floor of opus spicatum, 120 x 55 cm. Made with small bricks systemized in a fishbone pattern. The bricks measure 6.5 x 11.5 x 2.5 cm. Five rows running north-south are visible. Covered with mortar.', NULL, NULL, NULL, NULL, NULL, NULL, '2014-08-13 06:10:16', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (29, 1, E'0', E'0', NULL, E'0', E'Brick-faced opus caementicium wall, only visible on top. It runs from east to west, measuring 240 x 48 cm. It must belong to a building structure no longer recognizable in the field.', E'Wall structure', NULL, NULL, NULL, NULL, NULL, '2014-08-13 06:12:51', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (30, 1, E'0', E'0', E'Damaged', E'0', E'Square tufa block. Parts of corners and edges broken off. 52 x 66 x 60 cm. North side bevelled.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 06:17:20', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (31, 1, E'0', E'0', E'Damaged', E'0', E'Square marble block. East and south sides smooth, other sides broken off. 33 x 58 x 45 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 06:18:56', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (32, 1, E'0', E'0', E'Damaged', E'0', E'Part of tufa wall consisting of two tiers. The lower tier is barely visible under the second one which consists of two rectangular blocks (length 239 cm). The east side of the south block is roughly worked, south side broken off, whereas the top is roughly worked or heavily damaged. West side smooth (53 x 145 x 63 cm). The northern block is nothing more than a low chip (23 x 43 x 58 cm). It is in line with site 25.', E'Wall structure', NULL, NULL, NULL, NULL, NULL, '2014-08-13 06:19:49', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (33, 1, E'0', E'0', E'Damaged', E'0', E'Core of pillar tomb in opus caementicium composed of chips of basalt (ca 70 %), travertine (ca 20 %) and reddish tufa (10 %) in a bed of grey mortar. For measurements, see drawings. Five horizontal layers are visible separated by thin bands of fine white mortar. Top missing. The monument stands against the (older) Site 32.', E'Core', E'Funerary tower', NULL, NULL, NULL, NULL, '2014-08-13 06:22:23', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (34, 1, E'0', E'0', E'Damaged', E'0', E'Part of pulvinus. Broken off at sides. Chipped and covered with crust of moss. Tufa. 63 x 60 x 50 cm, diam. pulvinus 30 cm. Smooth on sides (one side visible) and bottom, top worked. Square plinth crowned with cylindrical element adorned with a pattern of overlapping pointed leaves. These have a central nerve and are barely recognizable. The piece clearly forms part of the adornment of a tomb in the shape of a pulvinus (cf. Sites 9 and 50).', NULL, E'Pulvinus', NULL, NULL, NULL, NULL, '2014-08-13 06:28:51', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (35, 1, E'0', E'0', E'Damaged', E'0', E'Site 35: Marble slab with inscription. Broken off at right side. Chips missing from left upper corner. 25 x 72 x 59 cm. Funerary epitaph on a flat slab surrounded by a cornice adorned with a cyma reversa. The letters gradually decreases in the lower lines. All words separated by dots. Right part of text missing (CIL VI 9796).\r\nC . LVTATIVS . C . [Lib]\r\nPAMPHILVS\r\nFRATRES . PIGME[ntariarii]\r\nVICI . LORARI . A[\r\nPOBLICIA LIB\r\nThis person belonged to a guild of pigmentarii and stemmed from the Vicus Lorarius, which we do not know very well (C. Lega, LTUR V, 175). The slab might come from Torre Selce (Spera/Mineo).', E'Slab', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:22:46', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (36, 1, E'0', E'0', E'Damaged', E'0', E'Tufa block. Broken off at all sides, but original surfaces partly preserved at east, west and south sides. 28 x 96 x 83 cm. Probably rectangular. Heavily damaged. North side shows a semicircular cavity of which it is not clear whether it is man-made or natural or even effect of damage.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:26:14', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (37, 1, E'0', E'0', E'Damaged', E'0', E'Rectangular tufa block. North-west top corner missing. Roughly worked at east and west sides. 36 x 105 x 50 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:29:04', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (38, 1, E'0', E'0', E'Damaged', E'0', E' Cornice or white marble entablature. Broken off at left side. Turned topside down. Bottom and right side smooth. Cavity hollowed out in back or caused by wear. Surface darkened by dirt. Chips missing from cornice. Tufa. 22 x 66 x 70 cm. Moulding composed, from top to bottom, of abacus, row of eggs and darts, row of dentils, cavetto. Seven eggs and eight dentils are still visible.', E'Edifice', E'Cornice', NULL, NULL, NULL, NULL, '2014-08-13 07:30:01', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (39, 1, E'0', E'0', E'Damaged', E'0', E'Core of pillar tomb in opus caementicium, rather preserved as a pile of material. It contains pieces of basalt, peperino and mortar. At the south side a plinth is visible with its corner at the east side. This block, broken in the middle, measures 220 cm. Three drips of lead are visible in its centre.', E'Core', E'Funerary tower', NULL, NULL, NULL, NULL, '2014-08-13 07:33:05', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (40, 1, E'0', E'0', E'Damaged', E'0', E'Tufa block. Bottom roughly worked at western side. Top and north west corners broken off. 13 (as visible) x 74 x 58 cm. Top is worn out.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:35:19', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (41, 1, E'0', E'0', E'Damaged', E'0', E'Corner of tufa block. Greater part missing. 24 (as visible) x 52 x 34 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:37:16', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (42, 1, E'0', E'0', NULL, E'0', E'Core of pillar tomb in opus caementicium, mainly of basalt chips, but at higher level red tufa and travertine chips have been inserted into the mortar. Six layers can be distinguished thanks to thin horizontal lines of fine mortar, viz. at the following heights from bottom to top: 58, 97, 131, 159, 190, 229 cm. They get thinner towards the top. As to measurements see drawing.', E'Core', E'Funerary tower', NULL, NULL, NULL, NULL, '2014-08-13 07:38:37', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (43, 1, E'0', E'0', E'Damaged', E'0', E'Two blocks\r\n43.1 > Tufa block. Only top visible. Irregularly broken off. South side worked. 34 x 36 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:41:00', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (43, 2, E'0', E'0', E'Damaged', E'0', E'43.2 > Rectangular tufa block. All sides broken off. 38 (as visible) x 61 x 48 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:41:56', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (44, 1, E'0', E'0', E'Damaged', E'0', E'Rectangular tufa block. All sides broken off or chipped. 55 (as visible) x 60 x 57 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:45:45', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (45, 1, E'0', E'0', NULL, E'0', E'Core of opus caementicium pillar or altar tomb mostly invisible under vegetation and with roundish outlines. Mainly composed of basalt chips and mortar.', E'Core', E'Funerary tower', NULL, NULL, NULL, NULL, '2014-08-13 07:47:15', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (46, 1, E'0', E'0', E'Damaged', E'0', E'Tufa block of architrave. Broken off at sides. Lying upside down. 66 x 55 (top), 40 (bottom) x 64 cm. Part of adorned architrave with moulding composed of quarter round and dentil (six dentils, of which one broken off visible). There are two dowel holes in the bottom, one in the north side. Round outlines indicate that it belonged to a (half-)round structure.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:48:18', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (47, 1, E'0', E'0', E'Damaged', E'0', E' Corner of architrave. Broken off at sides. Lying topside down. Tufa. 22 (as visible) x 77 x 55 cm. Right corner of architrave with decoration at square angles on front and right side. Moulding composed of quarter round, dentil and second quarter round.', NULL, NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:49:30', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (48, 1, E'0', E'0', E'Damaged', E'0', E'Amorphous tufa block. Broken off at all sides. 32 x 27 x 26 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:50:28', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (49, 1, E'0', E'0', E'Damaged', E'0', E'Site 49: Tufa block or slab. Broken off at all sides. Five amorphous pieces. 14 (as visible) x 109 x 40 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:51:15', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (50, 1, E'0', E'0', E'Complete', E'0', E'Pulvinus reused as urn holder. Complete. Surface damaged and chipped. Bottom interred; north side barely visible. Tufa. 48 x 41 x 234 cm, diam. pulvinus 19 cm.\r\nThe pulvinus is turned topside down. One distinguishes the rosette at the east side of the block and the pattern of pointed and superposed petals with central nerves behind. At the southern side this pattern, with six rows of leaves, runs some 63 cm, whereas at the north side it seems to cover the whole block). Consequently, the pulvinus must be imagined as decorating the left side of a tomb structure, the inner side not being visible from the ground.\r\nThe block got a second life when its original monument was apparently demolished and the block changed into an ollarium, a container of five ollae. The tops have a diameter of 19 cm, the first and third ones show a ring around the rim. The third ring, moreover, has two small holes for the insertion of clamps to close the lid. See for this sort of monuments, not rarely reused altars and other monuments Kammerer-Grothaus 2006b.', E'Block', E'Pulvinus', NULL, NULL, NULL, NULL, '2014-08-13 07:52:19', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (51, 1, E'0', E'0', E'Damaged', E'0', E'Rectangular marble block. Broken off at east side. Lower south-west corner missing. 28 x 62 x 38 cm.', E'Block', NULL, NULL, NULL, NULL, NULL, '2014-08-13 07:55:06', NULL);
