INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (54, 1, E'0', E'0', E'Damaged', E'0', E'Pulvinus. Broken off at east side. Surface chipped and covered with crust of moss. Tufa. 58 x 107 x 33 cm. Square rectangular base supporting pulvinus. The preserved part shows a segment of a bead, followed by two reels, a bead and reel. After this the cylindrical ornament (67 cm) covered with lancet-shaped petals, with a nerve in the centre, can be seen. The leaves cover each other.\r\nA team member suggested that the element might be a half pilaster leaning against a block, but one side is going over into the base and leaves no distinction to form a round or half-round engaged column.', E'Block', E'Pulvinus', NULL, NULL, NULL, NULL, '2014-08-13 10:48:28', NULL);
