INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (167, 1, E'0', E'0', E'Damaged', E'0', E'Wall composed of opus caementicium with tufa, basalt, brick, and marble bound by mortar. 150 x 44 x 450 cm. The wall runs east-west through the ditch and was heavily damaged when the ditch was dug.\r\nThe original construction is visible on the drawings by Labruzzi.', E'Wall structure', NULL, NULL, NULL, NULL, NULL, '2014-08-14 11:15:03', NULL);
