INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (932, 2, E'0', E'0', E'Damaged', E'1', E'East side. Ancient wall more or less completely preserved until top. Right upper part missing. Centre of tower part broken out and filled again later with roughly made wall; small window. Fine brickwork. Modulus 16 cm. Subdivided at height of second floor by means of cornice executed in moulded bricks. Profile: regula, fascia, two quarter rounds, regula, cyma recta.', E'Wall structure', NULL, E'East side of Laterizio II', NULL, NULL, NULL, '2014-07-30 13:40:23', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (932, 3, E'0', E'0', E'Complete', E'0', E'South side. West side shows medieval opus listatum followed by masonry as on north wall. Modern door. Some patches of fine original opus latericium preserved at east side. Modulus 16.5 cm. small window in upper part of wall.', E'Wall structure', NULL, E'South side of Laterizio II', NULL, NULL, NULL, '2014-07-30 13:43:18', NULL);
INSERT INTO "tbl1_object" ("Site_ID", "Object_ID", "In_situ", "Ancient", "Condition", "Modern_restoration", "Description_object", "Object_type", "Object_interpretation_list", "Object_interpretation", "Period", "Date_specific", "Material_remarks", "Date_entry", "description_restorations") VALUES (932, 4, E'0', E'0', E'Damaged', E'1', E'West side. Original opus caementicium visible in west corner, next to opus latericium (modulus 16.5 cm). For the greater part roughly restored. Three tiers of scaffolding holes in medieval masonry. Small window near core and door at north side.', E'Wall structure', NULL, E'West side of Laterizio II', NULL, NULL, NULL, '2014-07-30 13:45:13', NULL);
