INSERT INTO "tbl1_site" ("Site_ID", "Administrator", "Modern_composition", "Description_site", "Site_context", "Site_interpretation_keyword", "Site_interpretation", "Date_entry", "description_m_composition", "Description_site_OLD") VALUES (159, NULL, E'0', E'Block. Three sides smoothed. Other sides broken off. White marble with grey veins. 60 x 93 x 32 cm. Probably rectangular block with slanting side (120 degrees). Probably belonging to site 162.                 ', NULL, NULL, NULL, '2014-08-17 14:04:27', NULL, NULL);
