INSERT INTO "tbl2_object_inscription" ("ID", "Site_ID", "Object_ID", "Transcription", "Translation") VALUES (7, 811, 1, E'Inscription on top (H. letters 8 cm):\r\nPESIDIORVM\r\nInscription on side (H. letters 7 cm):\r\nL. OTIVS L. F.\r\nOTIA. L. F.', E'The first text refers to the collegium funeraticium of the Pesidii, which might point at people\r\nstemming from Pisidia (Asia Minor) and their funerary sodalicum. It might have been active\r\nbetween the 2nd and 4th century. The other text should be older, probably late Republican,\r\nsince the cognomen is lacking (Neudecker/Granino Cecere) so that the block clearly had two\r\nlives as epitaph.');
INSERT INTO "tbl2_object_inscription" ("ID", "Site_ID", "Object_ID", "Transcription", "Translation") VALUES (8, 580, 3, E'The text (CIL VI 17921) reads (height letters: 9 cm, line 2 and 3: 7 cm): \r\nT. FIDICLANIVS T. L. APELLA\r\nEX. TESTAMENTO. ARBITRATV\r\nFELICIS. ET. PHILARGYRI. L. ET. ATTICE. L.', E'(Titus Fidiclanius Apella, freedman of Titus, by testament. By the will of freedmen Felix and Philargyrus and freedwoman Attica)');
INSERT INTO "tbl2_object_inscription" ("ID", "Site_ID", "Object_ID", "Transcription", "Translation") VALUES (9, 601, 5, E'Within a cyma recta frame the text reads (height letters 9 cm; CIL VI 27959): L. VALERIVS L. L.\r\nBARICHA\r\nL. VALERIVS L. L.\r\nZABDA\r\nL. VALERIVS L. L.\r\nACHIBA', E'Baricha, Zabda and Achiba, liberti of Lucius Valerius');
INSERT INTO "tbl2_object_inscription" ("ID", "Site_ID", "Object_ID", "Transcription", "Translation") VALUES (10, 754, 1, E'ERE\r\nN. F. ANTO\r\nIN. FRONT. P. X[\r\nIN AGR. P. X', NULL);
INSERT INTO "tbl2_object_inscription" ("ID", "Site_ID", "Object_ID", "Transcription", "Translation") VALUES (11, 552, 2, E'(h. letters line 1 8.5 cm, line 2 7.0 cm, line 3 7.0 cm, line 4 6.0 cm; CIL I2 1250; VI 12289):\r\nL. ARELLIO. GLABRAI. L\r\nDIOPHANTO\r\nTITINAI. NOBILI\r\nVXSORI\r\n\r\nConnected to site 434. The inscription has been dated to the 1st century AD.', NULL);
INSERT INTO "tbl2_object_inscription" ("ID", "Site_ID", "Object_ID", "Transcription", "Translation") VALUES (12, 737, 3, E'NICIAE\r\nLACCI. ET\r\n(H. 24 cm, W. 109 cm, D. 63 cm.)', NULL);
